entity.snippet:
      public String get(String name) {
         return m_dynamicAttributes.get(name);
      }

      public AlertMetric set(String name, Object value) {
         if (value != null) {
            m_dynamicAttributes.put(name, String.valueOf(value));
         } else {
            m_dynamicAttributes.remove(name);
         }
         
         return this;
      }
